div.script:
(function ($) {
  var focusSearch = function() {
    if(!$('html').hasClass('showOverlay')) {
      $('html').addClass('showOverlay');
    }
  };
  var blurSearch = function() {
    $('html').removeClass('showOverlay');
  };
  $('input', '#frmSearch').off('focus.focusSearch', focusSearch).on('focus.focusSearch', focusSearch);
  $('.overlay').off('click.blurSearch', blurSearch).on('click.focusSearch', blurSearch);
})(jQuery);

var getTitles = function(e) {
  var n = e.feed.entry.length;
  var title = [];
  for (var i = 0; i < n; i++) {
    //console.log(e.feed.entry[i].link[4].href);
    title[i] = {
      data: e.feed.entry[i].title.$t,
      value: e.feed.entry[i].title.$t,
      href: e.feed.entry[i].link[4].href
    };
  }
  $('#txtSearch').autocomplete({
    lookup: title,
    onSelect: function(suggestion) {
      //console.log(suggestion);
      window.location.href = suggestion.href;
    }
  });
  $('#txtSearchMobile').autocomplete({
    lookup: title,
    onSelect: function(suggestion) {
      //console.log(suggestion);
      window.location.href = suggestion.href;
    }
  });
};
