var ry = '<h3 class="third-title"><span>Related Posts</span></h3>',
  rcomment = 'comments',
  rdisable = 'disable comments',
  commentYN = 'yes',
  dw = '',
  titles = [],
  titlesNum = 0,
  urls = [],
  timeR = [],
  thumb = [],
  commentsNum = [],
  comments = [];

function related_results_labels(c) {
  for (var b = 0; b < c.feed.entry.length; b++) {
    var d = c.feed.entry[b];
    titles[titlesNum] = d.title.$t;
    for (var a = 0; a < d.link.length; a++) {
      if ('thr$total' in d) { commentsNum[titlesNum] = d.thr$total.$t + ' ' + rcomment } else { commentsNum[titlesNum] = rdisable };
      if (d.link[a].rel == "alternate") {
        urls[titlesNum] = d.link[a].href;
        timeR[titlesNum] = d.published.$t;
        if ('media$thumbnail' in d) {
          thumb[titlesNum] = d.media$thumbnail.url;
        }
        else {
          thumb[titlesNum] = '//2.bp.blogspot.com/-pAkyABlSI9I/V5Vb3h5bgGI/AAAAAAAAEEg/03XLR_fUHfciuaylJJCi1GxDG2Lw9WqVwCLcB/s320/couper.jpg'
        }
        titlesNum++;
        break;
      }
    }
  }
}

function removeRelatedDuplicates() {
  var b = new Array(0);
  c = new Array(0);
  e = new Array(0);
  f = new Array(0);
  g = new Array(0);
  for (var a = 0; a < urls.length; a++) {
    if (!contains(b, urls[a])) {
      b.length += 1;
      b[b.length - 1] = urls[a];
      c.length += 1;
      c[c.length - 1] = titles[a];
      e.length += 1;
      e[e.length - 1] = timeR[a];
      f.length += 1;
      f[f.length - 1] = thumb[a];
      g.length += 1;
      g[g.length - 1] = commentsNum[a];
    }
  }
  urls = b;
  titles = c;
  timeR = e;
  thumb = f;
  commentsNum = g;
}

function contains(b, d) {
  for (var c = 0; c < b.length; c++) {
    if (b[c] == d) {
      return true;
    }
   }
  return false;
}

function printRelatedLabels(a) {
  var y = a.indexOf('?m=0');
  if (y != -1) {
    a = a.replace(/\?m=0/g, '');
  }
  for (var b = 0; b < urls.length; b++) {
    if (urls[b] == a) { urls.splice(b, 1);
      titles.splice(b, 1);
      timeR.splice(b, 1);
      thumb.splice(b, 1);
      commentsNum.splice(b, 1);
    }
  }
  var c = Math.floor((titles.length - 1) * Math.random());
  var b = 0;
  if (titles.length > 0) {
    dw += ry;
    dw += '<div class="outer row">';
    while (b < titles.length && b < 20 && b < maxresults) {
      if (y != -1) {
        urls[c] = urls[c] + '?m=0';
      }
      if (commentYN == 'yes') {
        comments[c] = ' - ' + commentsNum[c];
      }
      else {
        comments[c] = '';
      }
      dw += '<div class="col-md-3 col-sm-6"><div class="post">' +
              '<div class="inner">' +
                '<div class="thumbnail">' +
                  '<a href="' + urls[c] + '" rel="nofollow">' +
                    '<img alt="' + titles[c] + '" src="' + thumb[c].replace("/s72-c/", "/s300/") + '"/>' +
                  '</a>' +
                '</div>' +
                '<div class="content"><h3 class="title"><a href="' + urls[c] + '" rel="nofollow">' + titles[c] + '</a></h3></div>' +
              '</div>' +
            '</div></div>';
      if (c < titles.length - 1) {
        c++;
      }
      else {
        c = 0;
      }
      b++;
    }
    dw += '</div>'
  }
  urls.splice(0, urls.length);
  titles.splice(0, titles.length);
  document.getElementById('related-post').innerHTML = dw;
}
